    <script>
       const accessToken = '#';
       const userId = '#';
       async function fetchInstagramPosts() {
            try {
               const response = await fetch(`https://graph.instagram.com/${userId}/media?fields=id,media_url,timestamp,permalink&access_token=${accessToken}`);
               const data = await response.json();
               console.log('API Response:', data);
            if (data.error) {
               console.error('Error fetching data:', data.error.message);
               alert('Error fetching data: ' + data.error.message);
               return [];
            }
            return data.data;
            } catch (error) {
               console.error('Network error:', error);
               alert('Network error: ' + error.message);
               return [];
            }
        }

    function displayPosts(posts, containerId) {
       const container = document.getElementById(containerId);
       container.innerHTML = '';
       const maxPosts = 8;
       posts.slice(0, maxPosts).forEach(post => {
          const postElement = document.createElement('div');
          postElement.className = 'instagram-post';
          console.log('Image URL:', post.media_url);
          postElement.innerHTML = `
                     <a href="${post.permalink}" target="_blank">
                         <img src="${post.media_url}" alt="${post.caption || 'Instagram post image'}">
                     </a>
                     <p>${post.caption || ''}</p>
                 `;
          container.appendChild(postElement);
       });
    }
    async function init() {
       const posts = await fetchInstagramPosts();
       if (posts.length > 0) {
          posts.sort((a, b) => new Date(b.timestamp) - new Date(a.timestamp));
          console.log('Sorted Posts:', posts);
    
          displayPosts(posts, 'latest-posts');
       } else {
          console.log('No posts available.');
       }
    }
    
    window.onload = init;
</script>
